import React, { useState } from 'react'
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
}));


const AddUserForm = props => {
	const initialFormState = { id: null, accountName: '', accountNumber: '', address: '', city: '', country: '' }
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { id, value } = event.target

		setUser({ ...user, [id]: value })
	}

	return (
    // <div className={classes.root}
    //   onSubmit={event => {
    //     event.preventDefault()
    //     if (!user.accountName || !user.accountNumber) return

    //     props.addUser(user)
    //     setUser(initialFormState)
    //   }}
    // >
    //   <div>
    //     <TextField
    //       id="outlined-full-width"
    //       label="Label"
    //       style={{ margin: 8 }}
    //       helperText="Full width!"
    //       fullWidth
    //       className={classes.textField}
    //       margin="dense"
    //       InputLabelProps={{
    //         shrink: true,
    //       }}
    //       variant="outlined"
    //     />
    //   </div>
    // </div>
		<form
			onSubmit={event => {
				event.preventDefault()
				if (!user.accountName || !user.accountNumber) return

				props.addUser(user)
				setUser(initialFormState)
			}}
		>
			<Grid container spacing={3}>
        <Grid item xs={12}>
          <TextField id="outlined-full-width" label="Account Name" type="text" id="accountName" value={user.accountName} onChange={handleInputChange} variant="outlined" />
        </Grid>
        <Grid item xs={12}>
          <TextField label="Account Number" type="number" id="accountNumber" value={user.accountNumber} onChange={handleInputChange} variant="outlined" />
        </Grid>
        <Grid item xs={12}>
          <TextField label="Address" type="text" id="address" value={user.address} onChange={handleInputChange} variant="outlined" />
        </Grid>
        <Grid item xs={12}>
          <TextField label="City" type="text" id="city" value={user.city} onChange={handleInputChange} variant="outlined" />
        </Grid>
        <Grid item xs={12}>
          <TextField label="Country" type="text" id="country" value={user.country} onChange={handleInputChange} variant="outlined" />
        </Grid>
        <Grid item xs={12}>
          <Button color="primary" type="submit" variant="contained"> Add new account </Button>
        </Grid>
      </Grid>
		</form>
	)
}

export default AddUserForm
