import React, { useState } from 'react'

const AddUserForm = props => {
	const initialFormState = { id: null, accountName: '', accountNumber: '', address: '', city: '', country: '' }
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUser({ ...user, [name]: value })
	}

	return (
		<form
			onSubmit={event => {
				event.preventDefault()
				if (!user.accountName || !user.accountNumber || !user.address || !user.city || !user.country) return

				props.addUser(user)
				setUser(initialFormState)
			}}
		>
			<label>Account Name</label>
			<input type="text" name="accountName" value={user.accountName} onChange={handleInputChange} />
			<label>Account Number</label>
			<input type="number" name="accountNumber" value={user.accountNumber} onChange={handleInputChange} />
			<label>Address</label>
			<input type="text" name="address" value={user.address} onChange={handleInputChange} />
			<label>City</label>
			<input type="text" name="city" value={user.city} onChange={handleInputChange} />
			<label>Country</label>
			<input type="text" name="country" value={user.country} onChange={handleInputChange} />
			<button>Add new account</button>
		</form>
	)
}

export default AddUserForm
