import React, { useState } from 'react'
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';

const AddUserForm = props => {
  const initialUserState = { Name: 'Guest' }
	const initialFormState = { id: null, accountName: '', accountNumber: '', address: '', city: '', country: '' }
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { id, value } = event.target

		setUser({ ...user, [id]: value })
  }
  
  const cekUser = props => {
    if(initialUserState){
      console.log(initialUserState)
    }else{
      console.log('f')
    }
  };

	return (
    <form 
			onSubmit={event => {
        event.preventDefault()
        cekUser()
				if (!user.accountName || !user.accountNumber) return

				props.addUser(user)
				setUser(initialFormState)
			}}
		>
			<Grid container>
        <Grid item xs={12}>
          <TextField 
          fullWidth 
          margin="dense" 
          label="Account Name" 
          type="text" 
          id="accountName" 
          value={user.accountName} 
          onChange={handleInputChange} 
          variant="outlined" />
        </Grid>
        <Grid item xs={12}>
          <TextField 
          fullWidth 
          margin="dense" 
          label="Account Number" 
          type="number" 
          id="accountNumber" 
          value={user.accountNumber} 
          onChange={handleInputChange} 
          variant="outlined" />
        </Grid>
        <Grid item xs={12}>
          <TextField 
          fullWidth 
          margin="dense" 
          label="Address" 
          type="text" 
          id="address" 
          value={user.address} 
          onChange={handleInputChange} 
          variant="outlined" />
        </Grid>
        <Grid item xs={12}>
          <TextField 
          fullWidth 
          margin="dense" 
          label="City" 
          type="text" 
          id="city" 
          value={user.city} 
          onChange={handleInputChange} 
          variant="outlined" />
        </Grid>
        <Grid item xs={12}>
          <TextField 
          fullWidth 
          margin="dense" 
          label="Country" 
          type="text" 
          id="country" 
          value={user.country} 
          onChange={handleInputChange} 
          variant="outlined" />
        </Grid>
        <Grid item xs={12}>
          <Button color="primary" type="submit" variant="contained"> Add new account </Button>
        </Grid>
      </Grid>
    </form>
	)
}

export default AddUserForm
