import React, { useState } from 'react'
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import { Formik } from 'formik';
import * as Yup from 'yup';

const AddUserForm = props => {
	const initialFormState = { id: null, accountName: '', accountNumber: '', address: '', city: '', country: '' }
  const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUser({ ...user, [name]: value })
  }

  const Validation = Yup.object().shape({
    accountName: Yup.string()
        // .accountName()
        .required("Required")
        .max(10, "Password too long"),
    accountNumber: Yup.number()
        // .accountNumber()
        .required("Required")
        .min(8, "Account number too short"),
  });

	return (
    <Formik
      initialValues={{
        accountName: '',
        accountNumber: ''
      }}
      validationSchema={Validation}
      onSubmit={(user , { setSubmitting, resetForm }) => {
          this.onSubmit(user);
          setSubmitting(true);
          setTimeout((done) => {                                                            
              resetForm();
              setSubmitting(false);
          }, 500);
      }}
    >
      {({ values, errors, touched, handleChange, handleBlur, handleSubmit, isSubmitting}) => (    
        <form 
        onSubmit={event => {
          event.preventDefault()
  
          if (!user.accountName || !user.accountNumber) return
  
          props.addUser(user)
          setUser(initialFormState)
        }}
      >
        <Grid container>
          <Grid item xs={12}>
            <TextField 
            fullWidth 
            margin="dense" 
            label="Account Name" 
            type="text" 
            name="accountName" 
            value={values.accountName} 
            onChange={handleInputChange} 
            variant="outlined" 
            />
          </Grid>
          <Grid item xs={12}>
            <TextField 
            fullWidth 
            margin="dense" 
            label="Account Number" 
            type="number" 
            name="accountNumber" 
            value={values.accountNumber} 
            onChange={handleInputChange} 
            variant="outlined" 
            />
          </Grid>
          <Grid item xs={12}>
            <TextField 
            fullWidth 
            margin="dense" 
            label="Address" 
            type="text" 
            name="address" 
            value={user.address} 
            onChange={handleInputChange} 
            variant="outlined" />
          </Grid>
          <Grid item xs={12}>
            <TextField 
            fullWidth 
            margin="dense" 
            label="City" 
            type="text" 
            name="city" 
            value={user.city} 
            onChange={handleInputChange} 
            variant="outlined" />
          </Grid>
          <Grid item xs={12}>
            <TextField 
            fullWidth 
            margin="dense" 
            label="Country" 
            type="text" 
            name="country" 
            value={user.country} 
            onChange={handleInputChange} 
            variant="outlined" />
          </Grid>
          <Grid item xs={12}>
            <Button color="primary" type="submit" variant="contained"> Add new account </Button>
          </Grid>
        </Grid>
      </form>
      )}
    </Formik>
	)
}

export default AddUserForm
