import React, { useState, useEffect } from 'react'
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button'
import Grid from '@material-ui/core/Grid'
import { Formik } from 'formik';
import * as Yup from 'yup';


const EditUserForm = props => {
  const [ user, setUser ] = useState(props.currentUser)

  useEffect(
    () => {
      setUser(props.currentUser)
    },
    [ props ]
  )

  const Validation = Yup.object().shape({
    accountName: Yup.string()
        // .accountName()
        .required("Required")
        .max(10, "Password too long, max 10 characters"),
    accountNumber: Yup.string()
        // .accountNumber()
        .required("Required")
        .min(8, "Account number too short, min 8 characters"),
  });

  return (
    <Formik
      initialValues={{
        accountName: '',
        accountNumber: '',
        bankName: '',
        address: '',
        city: '',
        country: ''
      }}
      validationSchema={Validation}
      onSubmit={(values , { setSubmitting, resetForm }) => {
          // this.onSubmit(values);
          setSubmitting(true);
          console.log(values, 'ceebbb');
          props.updateUser(values)

          setTimeout((done) => {                                                            
              resetForm();
              setSubmitting(false);
          }, 500);
      }}
    >
      {({ values, errors, touched, handleChange, handleBlur, handleSubmit, isSubmitting}) => (    
      <form onSubmit={handleSubmit}>
        <Grid container>
          <Grid item xs={12}>
            <TextField 
            fullWidth 
            margin="dense" 
            label="Account Name" 
            type="text" 
            id="accountName" 
            value={values.accountName} 
            onChange={handleChange} 
            variant="outlined" />
          </Grid>
          <Grid item xs={12}>
            <TextField 
            fullWidth 
            margin="dense" 
            label="Account Number" 
            type="number" 
            id="accountNumber" 
            value={values.accountNumber} 
            onChange={handleChange} 
            variant="outlined" />
          </Grid>
          <Grid item xs={12}>
            <TextField 
            fullWidth 
            margin="dense" 
            label="Bank Name" 
            type="text" 
            id="bankName" 
            value={values.bankName} 
            onChange={handleChange} 
            variant="outlined" />
          </Grid>
          <Grid item xs={12}>
            <TextField 
            fullWidth 
            margin="dense" 
            label="Address" 
            type="text" 
            id="address" 
            value={values.address} 
            onChange={handleChange} 
            variant="outlined" />
          </Grid>
          <Grid item xs={12}>
            <TextField 
            fullWidth 
            margin="dense" 
            label="City" 
            type="text" 
            id="city" 
            value={values.city} 
            onChange={handleChange} 
            variant="outlined" />
          </Grid>
          <Grid item xs={12}>
            <TextField 
            fullWidth 
            margin="dense" 
            label="Country" 
            type="text" 
            id="country" 
            value={values.country} 
            onChange={handleChange} 
            variant="outlined" />
          </Grid>
          <Grid item xs={12}>
            <Button color="primary" type="submit" variant="contained"> Update account </Button>
            &nbsp;
            <Button color="primary" type="submit" variant="contained" onClick={() => props.setEditing(false)}> Cancel </Button>
          </Grid>
        </Grid>
      </form>
      )}
    </Formik>
  )
}

export default EditUserForm
