import React, { useState, Fragment } from 'react'
import AddUserForm from './forms/AddUserForm'
import EditUserForm from './forms/EditUserForm'
import UserTable from './tables/UserTable'
import UserCard from './user/UserCard'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';

const App = () => {
	// Data
	const usersData = [
		{ 
			id: 1, 
			accountName: 'Tania', 
			accountNumber: 12345, 
			address: 'California', 
			city: 'Malang', 
			country: 'Indonesia' 
    },
    { 
			id: 2, 
			accountName: 'Joko', 
			accountNumber: 9008, 
			address: 'Jalan', 
			city: 'Solo', 
			country: 'Japan' 
		},
	]

  const initialFormState = { id: null, accountName: '', accountNumber: '', address: '', city: '', country: '' }
  const initialGuestState = { Name: 'Guest' }

  // Setting state
  const [ guest, setGuest ] = useState(initialGuestState)
  const [editGuest, setEditingGuest] = useState(false)
	const [ users, setUsers ] = useState(usersData)
	const [ currentUser, setCurrentUser ] = useState(initialFormState)
  const [ editing, setEditing ] = useState(false)
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


	// CRUD
	const addUser = user => {
    if (guest.Name === 'Guest' || guest.Name === ''){
      // NotificationManager.success('Success message', 'Title here');
      return
    }
    // NotificationManager.success('Success message1', 'Title here');
		user.id = users.length + 1
		setUsers([ ...users, user ])
	}

	const deleteUser = id => {
		setEditing(false)

		setUsers(users.filter(user => user.id !== id))
  }


	const updateUser = (id, updatedUser) => {
		setEditing(false)

		setUsers(users.map(user => (user.id === id ? updatedUser : user)))
  }

  const updateGuest = (updateGuest) => {
    setEditingGuest(false)

		setGuest({Name : updateGuest})
	}

	const editRow = user => {
		setEditing(true)

    setCurrentUser({ 
      id: user.id, 
      accountName: user.accountName, 
      accountNumber: user.accountNumber,
      address: user.address,
      city: user.city,
      country: user.country 
    })
  }

	return (
    <div>
      <Grid container>
        <Grid item xs={12}>
        <Button variant="outlined" color="primary" onClick={handleClickOpen}>
          Open alert dialog
        </Button>
          <UserCard 
            guest={guest} 
            updateGuest={updateGuest} 
            editGuest={editGuest} 
            setEditingGuest={setEditingGuest}/>
          <Grid item xs={6}>
            <div>
              {editing ? (
                <Fragment>
                  <h2>Edit user</h2>
                  <EditUserForm
                    editing={editing}
                    setEditing={setEditing}
                    currentUser={currentUser}
                    updateUser={updateUser}
                  />
                </Fragment>
              ) : (
                <Fragment>
                  <h2>Add user</h2>
                  <AddUserForm addUser={addUser} />
                </Fragment>
              )}
            </div>
          </Grid>
          <Grid item xs={12}>
            <div>
              <h2>View users</h2>
              <UserTable users={users} editRow={editRow} deleteUser={deleteUser} />
            </div>
          </Grid>
        </Grid>
      </Grid>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Must be change the user first
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            close
          </Button>
        </DialogActions>
      </Dialog>
    </div>
	)
}

export default App
