import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles(theme => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: 200,
    },
  },
}));

const AddUserForm = props => {
	const initialFormState = { id: null, accountName: '', accountNumber: '', address: '', city: '', country: '' }
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUser({ ...user, [name]: value })
  }

  const classes = useStyles();

	return (
    <form className={classes.root} noValidate autoComplete="off"
      onSubmit={event => {
        event.preventDefault()
        if (!user.accountName || !user.accountNumber || !user.address || !user.city || !user.country) return

        props.addUser(user)
        setUser(initialFormState)
      }}
    >
			<TextField label="Account Name" type="text" name="accountName" value={user.accountName} onChange={handleInputChange}variant="outlined" />
      <TextField label="Account Number" type="number" name="accountNumber" value={user.accountNumber} onChange={handleInputChange}variant="outlined" />
      <TextField label="Address" type="text" name="address" value={user.address} onChange={handleInputChange}variant="outlined" />
      <TextField label="City" type="text" name="city" value={user.city} onChange={handleInputChange}variant="outlined" />
      <TextField label="Country" type="text" name="country" value={user.country} onChange={handleInputChange}variant="outlined" />
      <Button variant="contained" color="primary"> Add new account </Button>
    </form>

	)
}

export default AddUserForm
