import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles(theme => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
    },
  },
}));

const AddUserForm = props => {
	const initialFormState = { id: null, accountName: '', accountNumber: '', address: '', city: '', country: '' }
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { id, value } = event.target

		setUser({ ...user, [id]: value })
  }

  const classes = useStyles();

	return (
    <div className={classes.root}
      onSubmit={event => {
        event.preventDefault()
        if (!user.accountName || !user.accountNumber || !user.address || !user.city || !user.country) return

        props.addUser(user)
        setUser(initialFormState)
      }}
    >
      <Grid container spacing={3}>
        <Grid item xs={2}>
          <TextField label="Account Name" type="text" id="accountName" value={user.accountName} onChange={handleInputChange}variant="outlined" />
        </Grid>
        <Grid item xs={2}>
          <TextField label="Account Number" type="number" id="accountNumber" value={user.accountNumber} onChange={handleInputChange}variant="outlined" />
        </Grid>
        <Grid item xs={2}>
          <TextField label="Address" type="text" id="address" value={user.address} onChange={handleInputChange}variant="outlined" />
        </Grid>
        <Grid item xs={2}>
          <TextField label="City" type="text" id="city" value={user.city} onChange={handleInputChange}variant="outlined" />
        </Grid>
        <Grid item xs={2}>
          <TextField label="Country" type="text" id="country" value={user.country} onChange={handleInputChange}variant="outlined" />
        </Grid>
        <Grid item xs={12}>
          <Button color="primary" type="submit" variant="contained"> Add new account </Button>
          <button>Add new user</button>
        </Grid>
      </Grid>
    </div>

	)
}

export default AddUserForm
